function STCNSTT_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('STCNSTT');
	if(!$arrPerm['insert']) return false;

	$data = [
		'Phanloai' => Request::val('Phanloai', ''),
		'Danhmuc' => Request::val('Danhmuc', ''),
		'Heloai' => Request::val('Heloai', ''),
		'IDSTCN' => Request::lookup('IDSTCN', ''),
	];


	// hook: STCNSTT_before_insert
	if(function_exists('STCNSTT_before_insert')) {
		$args = [];
		if(!STCNSTT_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('STCNSTT', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('STCNSTT', $recID, calculated_fields()['STCNSTT']);

	// hook: STCNSTT_after_insert
	if(function_exists('STCNSTT_after_insert')) {
		$res = sql("SELECT * FROM `STCNSTT` WHERE `IDSTT`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!STCNSTT_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('STCNSTT', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) STCNSTT_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function STCNSTT_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function STCNSTT_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('STCNSTT', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: STCNSTT_before_delete
	if(function_exists('STCNSTT_before_delete')) {
		$args = [];
		if(!STCNSTT_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `STCNSTT` WHERE `IDSTT`='{$selected_id}'", $eo);

	// hook: STCNSTT_after_delete
	if(function_exists('STCNSTT_after_delete')) {
		$args = [];
		STCNSTT_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='STCNSTT' AND `pkValue`='{$selected_id}'", $eo);
}

function STCNSTT_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('STCNSTT', $selected_id, 'edit')) return false;

	$data = [
		'Phanloai' => Request::val('Phanloai', ''),
		'Danhmuc' => Request::val('Danhmuc', ''),
		'Heloai' => Request::val('Heloai', ''),
		'IDSTCN' => Request::lookup('IDSTCN', ''),
	];

	// get existing values
	$old_data = getRecord('STCNSTT', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: STCNSTT_before_update
	if(function_exists('STCNSTT_before_update')) {
		$args = ['old_data' => $old_data];
		if(!STCNSTT_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'STCNSTT', 
		backtick_keys_once($set), 
		['`IDSTT`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="STCNSTT_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('STCNSTT', $data['selectedID'], calculated_fields()['STCNSTT']);

	// hook: STCNSTT_after_update
	if(function_exists('STCNSTT_after_update')) {
		$res = sql("SELECT * FROM `STCNSTT` WHERE `IDSTT`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['IDSTT'];
		$args = ['old_data' => $old_data];
		if(!STCNSTT_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='STCNSTT' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

