	$templateCode = str_replace('<%%COMBOTEXT(Heloai)%%>', $combo_Heloai->SelectedData, $templateCode);
	$templateCode = str_replace('<%%COMBO(IDSTCN)%%>', $combo_IDSTCN->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(IDSTCN)%%>', $combo_IDSTCN->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(IDSTCN)%%>', urlencode($combo_IDSTCN->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['IDSTCN' => ['STCN', 'IDSTCN'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(IDSTT)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Phanloai)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Danhmuc)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Heloai)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(IDSTCN)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(IDSTT)%%>', safe_html($urow['IDSTT']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(IDSTT)%%>', html_attr($row['IDSTT']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(IDSTT)%%>', urlencode($urow['IDSTT']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Phanloai)%%>', safe_html($urow['Phanloai']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Phanloai)%%>', html_attr($row['Phanloai']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Phanloai)%%>', urlencode($urow['Phanloai']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Danhmuc)%%>', safe_html($urow['Danhmuc']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Danhmuc)%%>', html_attr($row['Danhmuc']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Danhmuc)%%>', urlencode($urow['Danhmuc']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Heloai)%%>', safe_html($urow['Heloai']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Heloai)%%>', html_attr($row['Heloai']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Heloai)%%>', urlencode($urow['Heloai']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(IDSTCN)%%>', safe_html($urow['IDSTCN']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(IDSTCN)%%>', html_attr($row['IDSTCN']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(IDSTCN)%%>', urlencode($urow['IDSTCN']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(IDSTT)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(IDSTT)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Phanloai)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Phanloai)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Danhmuc)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Danhmuc)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Heloai)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Heloai)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(IDSTCN)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(IDSTCN)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('STCNSTT');
	if($selected_id) {
		$jdata = get_joined_record('STCNSTT', $selected_id);
		if($jdata === false) $jdata = get_defaults('STCNSTT');
		$rdata = $row;
	}
	$templateCode .= loadView('STCNSTT-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: STCNSTT_dv
	if(function_exists('STCNSTT_dv')) {
		$args = [];
		STCNSTT_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}